public class GreatestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 100;
		int b= 15;
		int c =1500;
		
		if (a>b && a>c)
		{
			System.out.println(a);
		}
		else if (b>a && b>c)
		{
			System.out.println(b);
			
		}
		else
			System.out.println(c);
			

	}

}
